pragma solidity ^0.8.3;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

contract Airdrop {
  address public admin;
  mapping(address => bool) public processedAirdrops;
  IERC20 public token;
  uint public currentAirdropAmount;
  uint public maxAirdropAmount ;

  event AirdropProcessed(
    address recipient,
    uint amount,
    uint date
  );

  constructor(address _token, address _admin, uint _maxAirdropAmount) {
    admin = _admin; 
    token = IERC20(_token);
    maxAirdropAmount = _maxAirdropAmount * 10 ** 18;
  }

  function updateAdmin(address newAdmin) external {
    require(msg.sender == admin, 'only admin');
    admin = newAdmin;
  }

  function claimTokens(
    address recipient,
    uint amount,
    bytes calldata signature
  ) external {
    bytes32 message = prefixed(keccak256(abi.encodePacked(
      recipient, 
      amount
    )));
    require(recoverSigner(message, signature) == admin , 'wrong signature');
    require(processedAirdrops[recipient] == false, 'airdrop already processed');
    require(currentAirdropAmount + amount <= maxAirdropAmount, 'airdropped 100% of the tokens');
    processedAirdrops[recipient] = true;
    currentAirdropAmount += amount;
    token.transfer(recipient, amount);
    emit AirdropProcessed(
      recipient,
      amount,
      block.timestamp
    );
  }

  function prefixed(bytes32 hash) internal pure returns (bytes32) {
    return keccak256(abi.encodePacked(
      '\x19Ethereum Signed Message:\n32', 
      hash
    ));
  }

  function recoverSigner(bytes32 message, bytes memory sig)
    internal
    pure
    returns (address)
  {
    uint8 v;
    bytes32 r;
    bytes32 s;

    (v, r, s) = splitSignature(sig);

    return ecrecover(message, v, r, s);
  }

  function splitSignature(bytes memory sig)
    internal
    pure
    returns (uint8, bytes32, bytes32)
  {
    require(sig.length == 65);

    bytes32 r;
    bytes32 s;
    uint8 v;

    assembly {
        // first 32 bytes, after the length prefix
        r := mload(add(sig, 32))
        // second 32 bytes
        s := mload(add(sig, 64))
        // final byte (first byte of the next 32 bytes)
        v := byte(0, mload(add(sig, 96)))
    }

    return (v, r, s);
  }
}
